mapper.select:
SELECT count(1) FROM ACT_HI_TASKINST T, ACT_HI_PROCINST P, sys_user U, ACT_RE_PROCDEF RP, ACT_RE_DEPLOYMENT D WHERE T.OWNER_ = #{userId,jdbcType=INTEGER} AND T.PROC_INST_ID_ = P.ID_ AND P.START_USER_ID_ = U.id AND T.PROC_DEF_ID_ = RP.ID_ AND RP.DEPLOYMENT_ID_ = D.ID_
AND D.NAME_ LIKE CONCAT('%',#{param.name,jdbcType=VARCHAR},'%')
AND U.name LIKE CONCAT('%',#{param.user,jdbcType=VARCHAR},'%')
AND T.NAME_ LIKE CONCAT('%',#{param.task,jdbcType=VARCHAR},'%')
AND P.BUSINESS_KEY_ = #{param.level,jdbcType=INTEGER}
AND P.START_TIME_ > #{param.start,jdbcType=TIMESTAMP}
AND P.START_TIME_ < #{param.end,jdbcType=TIMESTAMP}

SELECT T.ID_, T.REV_, T.PROC_DEF_ID_, RP.KEY_ PROC_DEF_KEY_, D.NAME_ PROC_DEF_NAME_, T.TASK_DEF_ID_, T.TASK_DEF_KEY_, T.PROC_INST_ID_, T.EXECUTION_ID_, T.SCOPE_ID_, T.SUB_SCOPE_ID_, T.SCOPE_TYPE_, T.SCOPE_DEFINITION_ID_, T.NAME_, T.PARENT_TASK_ID_, T.DESCRIPTION_, T.OWNER_, T.ASSIGNEE_, T.START_TIME_, T.CLAIM_TIME_, T.END_TIME_, T.DURATION_, T.DELETE_REASON_, T.PRIORITY_, T.DUE_DATE_, T.FORM_KEY_, T.CATEGORY_, T.TENANT_ID_, T.LAST_UPDATED_TIME_, U.name CREATE_USER_, U.dept_name CREATE_DEPT_, P.START_TIME_ CREATE_TIME_, P.BUSINESS_KEY_ LEVEL_ FROM ACT_HI_TASKINST T, ACT_HI_PROCINST P, sys_user U, ACT_RE_PROCDEF RP, ACT_RE_DEPLOYMENT D WHERE T.OWNER_ = #{userId,jdbcType=INTEGER} AND T.PROC_INST_ID_ = P.ID_ AND P.START_USER_ID_ = U.id AND T.PROC_DEF_ID_ = RP.ID_ AND RP.DEPLOYMENT_ID_ = D.ID_
AND D.NAME_ LIKE CONCAT('%',#{param.name,jdbcType=VARCHAR},'%')
AND U.name LIKE CONCAT('%',#{param.user,jdbcType=VARCHAR},'%')
AND T.NAME_ LIKE CONCAT('%',#{param.task,jdbcType=VARCHAR},'%')
AND P.BUSINESS_KEY_ = #{param.level,jdbcType=INTEGER}
AND P.START_TIME_ > #{param.start,jdbcType=TIMESTAMP}
AND P.START_TIME_ < #{param.end,jdbcType=TIMESTAMP}
ORDER BY T.END_TIME_ DESC LIMIT #{page.offset,jdbcType=INTEGER}, #{page.size,jdbcType=INTEGER}
